    private void SkaitlaApstradeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SkaitlaApstradeActionPerformed
        // TODO add your handling code here:
        
         String skaitlis = Skaitlis.getText();
        
        if (skaitlis.matches(".*[a-zA-Z]+.*")){
            SkaitlaIzvade.setText("Ievadītā vērtība nav skaitlis");
        }
        
        int cipari = Integer.parseInt(skaitlis);

        if (cipari > 5){
            SkaitlaIzvade.setText("Skaitlis ir lielāks par 5");
        }else if ( cipari == 5) {
            SkaitlaIzvade.setText("Skaitlis ir vienāds ar 5");
        }else if (cipari < 5){
            SkaitlaIzvade.setText("Skaitlis ir mazāks par 5");
        }
            
    }//GEN-LAST:event_SkaitlaApstradeActionPerformed

