    private void TekstaApstradeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_TekstaApstradeActionPerformed
        // TODO add your handling code here:
        
           String teksts = Teksts.getText();
        
        if( teksts.charAt(0) == teksts.toUpperCase().charAt(0)){
            TekstaIzvade.setText(teksts.toUpperCase());
        }else{
            TekstaIzvade.setText("Ievadi vārdu ar lielo burtu");
        }
    }//GEN-LAST:event_TekstaApstradeActionPerformed


